INSERT INTO Content (id, data) VALUES ('service-fysiotherapie', '{"title":"FYSIOTHERAPIE","subtitle":"Fysiotherapeutisch trainen bij Fytaal","content":[{"type":"bold","text":"Van klacht naar kracht ‚Äì herstel door bewegen"},{"type":"p","text":"Bewegen lijkt vanzelfsprekend. Tot het even niet meer lukt door een blessure, operatie of langdurige overbelasting. Juist dan wil je snel en effectief herstellen. Bij Fytaal combineren we fysiotherapie met doelgericht trainen. Zo min mogelijk passieve behandelingen, maar actief aan de slag om weer vertrouwen te krijgen in je lichaam en sterker dan ooit terug te komen."},{"type":"divider"},{"type":"h3","text":"Fysiotherapie & Medical Training"},{"type":"p","text":"Per 1 januari aanstaande verandert de invulling van fysiotherapie binnen Fytaal."},{"type":"p","text":"Vanaf dat moment worden alle fysiotherapeutische behandelingen uitgevoerd door MA Visio. Deze samenwerking sluit naadloos aan bij de visie van Fytaal: duurzaam herstel, persoonlijke begeleiding en doelgericht trainen."},{"type":"bold","text":"Fytaal blijft daarnaast Medical Personal Training aanbieden."},{"type":"p","text":"Deze vorm van training is bedoeld voor mensen die herstellen van een blessure, klachten hebben of vanuit een medische achtergrond willen (blijven) trainen."}],"cta":"MAAK EEN AFSPRAAK","image":"/381A1610-1-edited-2048x1152.webp"}');
INSERT INTO Content (id, data) VALUES ('service-personal-training', '{"title":"PERSONAL TRAINING","subtitle":"Gericht. Persoonlijk. Effectief.","content":[{"type":"p","text":"Wil je werken aan je kracht, conditie, fitheid of herstel? Personal Training is de meest effectieve manier om jouw fysieke of sportieve doelstellingen te behalen."},{"type":"bold","text":"Bij Fytaal draait alles om maatwerk."},{"type":"p","text":"Geen standaard schema‚Äôs, maar een persoonlijk plan op basis van jouw doelen, belastbaarheid en eventuele blessures of (chronische) klachten."},{"type":"p","text":"Je traint altijd samen met een deskundige coach die jou motiveert, bijstuurt en uitdaagt om het beste uit jezelf te halen."},{"type":"h3","text":"Onze aanpak: Functioneel √©n slim trainen"},{"type":"p","text":"Bij Fytaal geloven we in functioneel trainen: oefeningen die jouw lichaam sterker maken voor bewegingen in het dagelijks leven. Denk aan kracht, snelheid, co√∂rdinatie en uithoudingsvermogen."},{"type":"p","text":"Daarbij maken we onder andere gebruik van Keiser apparatuur met luchtdrukweerstand. Deze geavanceerde technologie is:"},{"type":"list","items":["Veilig voor gewrichten, pezen en banden ‚Äì ideaal bij revalidatie;","Perfect voor sporters ‚Äì explosieve en snelle krachttraining;","Effectief voor iedereen ‚Äì kracht √©n snelheid zijn de eerste fysieke vermogens die afnemen als je ouder wordt. Wij helpen je om dat te vertragen of zelfs te verbeteren."]},{"type":"h3","text":"Hoe ziet Personal Training eruit?"},{"type":"p","text":"Je kunt kiezen uit:"},{"type":"list","items":["40 minuten Personal Training","60 minuten Personal Training","Liever samen trainen? Dat kan ook met onze Duo Personal Training of in combinatie met onze Private Group Trainingen (max. 4 personen)."]},{"type":"h3","text":"Tarieven"},{"type":"list","items":["40 min Personal Training: vanaf ‚Ç¨ 47,67 per training","60 min Personal Training: vanaf ‚Ç¨ 70,93 per training","Duo Training (40 min): vanaf ‚Ç¨ 25,97 per persoon per training"]}],"cta":"BOEK EEN KENNISMAKINGSGESPREK","image":"/Koen-683x1024.webp"}');
INSERT INTO Content (id, data) VALUES ('service-vitaliteit', '{"title":"LEEFSTIJLCOACHING","subtitle":"LeefstijlCoaching bij Fytaal","content":[{"type":"h3","text":"Voor wie zich weer krachtig, energiek en in balans wil voelen"},{"type":"p","text":"Gezond en fit ouder worden ‚Äì dat willen we allemaal. Maar tussen weten wat goed voor je is, en het ook doen, zit vaak een flinke kloof."},{"type":"p","text":"Herken jij jezelf in een van de volgende uitspraken?"},{"type":"list","items":["Ik wil me weer energiek en krachtig voelen.","Ik wil het maximale uit mijn leven halen.","Ik wil met mijn (klein)kinderen buiten kunnen spelen.","Ik wil mijn balans terugvinden."]},{"type":"p","text":"Dan is leefstijlcoaching bij Fytaal iets voor jou."},{"type":"bold","text":"Geen hype, maar blijvende verandering"},{"type":"p","text":"Vitaliteit draait om meer dan alleen voeding of beweging. Hoe jij je voelt wordt be√Ønvloed door alles wat je dagelijks doet."},{"type":"divider"},{"type":"bolditalic","text":"Vergeet de quick fix. Ga voor een blijvende verandering."}],"cta":"AANVRAGEN","image":"/Doriene-683x1024.webp"}');
INSERT INTO Content (id, data) VALUES ('service-hyrox', '{"title":"HYROX","subtitle":"Offici√´le Hyrox Training Club","content":[{"type":"bold","text":"Wij zijn nu een offici√´le Hyrox Training Club!"},{"type":"p","text":"Klaar voor de ultieme uitdaging? Vanaf nu train je bij ons gericht voor Hyrox. Functioneel, intens, en altijd met een doel. Of je nu je eerste race wilt finishen of je PR wilt verbreken ‚Äî bij ons word je sterker, sneller en fitter dan ooit."},{"type":"h3","text":"üí™ Wat is Hyrox?"},{"type":"p","text":"Hyrox combineert 8 x 1 km hardlopen met 8 zware workouts zoals:"},{"type":"list","items":["Sled Push & Pull","Lunges","Farmers Carry","Wall Balls"]},{"type":"p","text":"Een unieke combinatie van kracht en conditie. Geen quick fix, maar echte prestaties."},{"type":"h3","text":"‚úÖ Waarom trainen bij ons?"},{"type":"list","items":["Gerichte Hyrox trainingen (met coaching & techniek)","Oefenen op alle onderdelen uit de race","Kleine groepen, persoonlijke aandacht","Sterke community met √©√©n doel: beter worden"]},{"type":"h3","text":"üöÄ Voor wie?"},{"type":"list","items":["Beginners die kennis willen maken met Hyrox","Sporters die zich serieus willen voorbereiden op een race","Iedereen die houdt van een next-level full body workout"]}],"cta":"BOEK EEN KENNISMAKINGSGESPREK","image":"/472453920-682x1024.jpg","objectPosition":"center 20%"}');
INSERT INTO Content (id, data) VALUES ('service-personal-group-training', '{"title":"PERSONAL GROUP TRAINING","subtitle":"Persoonlijke aandacht, energie van de groep","content":[{"type":"bold","text":"De persoonlijke aanpak van Personal Training, met de energie van een groep"},{"type":"p","text":"Wil jij je fitter en energieker voelen, maar lukt het je niet om dit zelfstandig vol te houden? Vind je het leuker om samen met anderen te sporten, maar wil je w√©l persoonlijke begeleiding? Dan is Personal Group Training bij Fytaal precies wat je zoekt."},{"type":"h3","text":"Train in een kleine, hechte groep"},{"type":"p","text":"Onze Personal Group Trainingen zijn kleinschalige trainingen in groepen van maximaal 8 personen. Door deze opzet is er volop persoonlijke aandacht voor jou en je techniek ‚Äî meer dan bij een reguliere groepsles of sportschool."},{"type":"h3","text":"Onze trainingen:"},{"type":"list","items":["Circuit Basic: Full body workout met focus op core & posture.","Circuit Plus: Gevarieerde circuittraining voor meer power.","Strength: Krachttraining met een pittige conditionele finisher.","Conditioning: Interval of endurance training.","Functional: Krachttraining met o.a. onze high-end Keiser apparatuur.","Fytter (Fysiofitness): Voor wie wil trainen onder begeleiding van een fysiotherapeut."]},{"type":"h3","text":"Waarom kiezen voor Personal Group Training bij Fytaal?"},{"type":"list","items":["Persoonlijke begeleiding binnen een kleine groep","Doelgerichte trainingen voor elk niveau","Meer motivatie door samen te trainen","Betaalbaar alternatief voor Personal Training"]}],"cta":"ROOSTER BEKIJKEN","ctaLink":"/rooster","image":"/381A1610-1-edited-2048x1152.webp"}');
